import React, { useState, useEffect } from "react";

const IS_LOGGIN = "isLoggeIn";
const AuthContext = React.createContext({ isLoggIn: false });

export const AuthContextProvider = (props) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const userLoggingInfo = localStorage.getItem(IS_LOGGIN);
    if (userLoggingInfo === "1") setIsLoggedIn(true);
  }, []);

  const loginHandler = (email, password) => {
    // We should of course check email and password
    // But it's just a dummy/ demo anyways
    localStorage.setItem(IS_LOGGIN, "1");
    setIsLoggedIn(true);
  };

  const logoutHandler = () => {
    localStorage.removeItem(IS_LOGGIN);
    setIsLoggedIn(false);
  };

  return (
    <AuthContext.Provider
      value={{
        isLoggIn: isLoggedIn,
        onLogin: logoutHandler,
        onLogout: logoutHandler,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
