import { useState, useRef } from "react";

const SimpleInput = (props) => {
  const [enterName, setEnterName] = useState({ name: "" });
  const [nameIsValid, setNameIsValid] = useState(false);
  const [inputTouched, setInputTouched] = useState(false);
  const nameRef = useRef();

  const changeHandler = (e) => {
    setEnterName((prev) => {
      return { ...prev, name: e.target.value };
    });
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();

    if (enterName.name === "") {
      setNameIsValid(false);
      return;
    }

    setNameIsValid(true);
    console.log(enterName.name);
    console.log(nameRef.current.value);
    setEnterName({ ...enterName, name: "" });
  };

  const inputNameClasses = nameIsValid
    ? "form-control"
    : "form-control invalid";
  return (
    <form onSubmit={onSubmitHandler}>
      <div className={inputNameClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          ref={nameRef}
          id="name"
          onChange={changeHandler}
          value={enterName.name}
        />
      </div>
      {!nameIsValid && <p className="error-text">Name most not be empty</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
