import { createSlice, configureStore } from "@reduxjs/toolkit";

const countInitialState = { counter: 0, showCounter: true };

const counterSlide = createSlice({
  name: "counter",
  initialState: countInitialState,
  reducers: {
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter = state.counter + action.payload;
    },
    showCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});

const authInitialState = { authenticated: false };
const authSlide = createSlice({
  name: "auth",
  initialState: authInitialState,
  reducers: {
    login(state) {
      state.authenticated = true;
    },
    logout(state) {
      state.authenticated = false;
    },
  },
});

const store = configureStore({
  reducer: { counter: counterSlide.reducer, auth: authSlide.reducer },
});
export const counterAction = counterSlide.actions;
export const authAction = authSlide.actions;

export default store;
